    @Test
    void testAddFavouriteCity3() {
        // Arrange
        Mono<Object> mono = mock(Mono.class);
        ChannelSendOperator<Object> channelSendOperator = new ChannelSendOperator<>(mock(Publisher.class),
            mock(Function.class));

        when(mono.then()).thenReturn(channelSendOperator);
        Mono<UserEntity> mono2 = mock(Mono.class);
        when(mono2.flatMap(Mockito.<Function<UserEntity, Mono<Object>>>any())).thenReturn(mono);
        when(userRepository.findById(Mockito.<UUID>any())).thenReturn(mono2);
        UUID userId = UUID.randomUUID();

        City city = new City();
        city.setName("Name");
        city.setUuid(UUID.randomUUID());

        // Act and Assert
        assertSame(channelSendOperator, userService.addFavouriteCity(userId, city));
        verify(userRepository).findById(Mockito.<UUID>any());
        verify(mono2).flatMap(Mockito.<Function<UserEntity, Mono<Object>>>any());
        verify(mono).then();
    }

